import './App.css';
import axios from 'axios';
import {useState} from 'react';
import { useQuery } from "react-query";
const fetchLamp = async () => {
  const result = await axios.get("http://127.0.0.1:3001/lamp",{headers:{"Content-Type" :'application/json'}});
  return result.data;
}


function App() {
  const [modify,setModify] = useState(false)
  const handleClick = () => setModify( current => !current )
  const { isLoading, error, data, isFetching } = useQuery('lamp',fetchLamp)
  if (isLoading) return "Loading...";

    if (error) return "An error has occurred: " + error.message;


  return (
    <div className="App">
     {modify ? 
     <div><p>Id lampada:{data.lamp_id}</p>
      <p>Livello di luminosità:{data.brightness}</p>
      {data.lamp_status ? <p>Acceso</p>:<p>Spento</p>}
    <button onClick={() => handleClick()} >Cambia valori</button></div>
    : 
    <form onSubmit={handleSubmit}>
          <label htmlFor="luminosità">Luminosità:</label>
          <input id="luminosità" type="number" value={idolName} onChange={(e) => setIdolName(e.target.value)}></input>
          <label htmlFor="idolGroup">From:</label>
          <input id="idolGroup" type="text" value={idolGroup} onChange={(e) => setIdolGroup(e.target.value)}></input>
    </form>
      }

    </div>
  );
}

export default App;
