import axios from "axios";
import {useState} from 'react';
import { useQuery } from "react-query";

export function GestioneAree() {
    const [areaList,setAreaList] = useState([]);
    
    const { isLoading, error , data, isFetching } = useQuery(["area"], () =>
    axios.get("http://localhost:3002/aree")
    .then((res) => setAreaList(res.data))
    )
    const handleClickOnArea= async () => {
        const [lampList,setLampList] = useState([]);
        const {isLoading, error} = useQuery(["lamp"], () =>
        axios.get("http://localhost:3002/lamp")
        .then((res) => setAreaList(res.data))
        )
        return 
    }
    if (error) return "An error has occurred: " + error.message;
    return(
        <div>
            <p>Lista aree</p>
            {isLoading ? <p>Loading...</p>:
            <ul>
                {areaList.map(area => (
                    <li key={area.ID}>
                        <button value={area.ID} onClick={e => handleClickOnArea(e.target.value)}>{area.ID} {area.zona_geografica}</button>
                    </li>
                ) )}
            </ul>}
            
        </div>
    )
    
}
