import axios from "axios";
import {useState} from 'react';
import { useQuery } from "react-query";
const headers = {
    
    'Content-Type': 'application/json'
  
}

export async function Area(areaId){
    const [lampList,setLampList] = useState([]);
        const {isLoading, error} = useQuery(["lamp"], () =>
        axios.get("http://localhost:3002/lamp/"+areaId,{headers})
        .then((res) => setLampList(res.data))
        )
        return (
            <div>
            <p>Lista aree</p>
            {isLoading ? <p>Loading...</p>:
            <ul>
                {areaList.map(area => (
                    <li key={area.ID}>
                        <button value={area.ID} onClick={e => Area(e.target.value)}>{area.ID} {area.zona_geografica}</button>
                    </li>
                ) )}
            </ul>}
            
        </div>
        )
}
